mapper.select:
SELECT A.RN, A.S_NAME, A.S_IS_OPEN, A.S_CODE, A.S_PHONE, A.S_ADDRESS, A.S_BIZNUMBER, A.S_X, A.S_Y, A.MENU_CODE, A.MENU_NAME, A.MENU_PRICE, A.ISORDER FROM ( SELECT ROWNUM RN, C.S_NAME, C.S_IS_OPEN, C.S_CODE, C.S_PHONE, C.S_ADDRESS, C.S_BIZNUMBER, C.S_X, C.S_Y, D.MENU_CODE, D.MENU_NAME, D.MENU_PRICE, D.ISORDER, DISTNACE_WGS84(#{location.sY}, #{location.sX}, C.S_Y, C.S_X) AS distance -- 거리 FROM ( SELECT C.S_NAME, C.S_IS_OPEN, C.S_CODE, C.S_PHONE, C.S_ADDRESS, C.S_BIZNUMBER, C.S_X, C.S_Y FROM STORE C WHERE (C.S_Y BETWEEN #{location.sY} - 0.019 AND #{location.sY} + 0.019) -- 위도 약 2km 이내 AND (C.S_X BETWEEN #{location.sX} - 0.022 AND #{location.sX} + 0.022) -- 경도 약 2km 이내
AND C.S_NAME LIKE '%' || #{criteria.searchValue} || '%'
) C LEFT JOIN ( SELECT S_CODE, MIN(MENU_CODE) AS MENU_CODE, MIN(MENU_NAME) AS MENU_NAME, MIN(MENU_PRICE) AS MENU_PRICE, MIN(ISORDER) AS ISORDER FROM MENU
1=1
AND MENU_NAME LIKE '%' || #{searchValue} || '%'
GROUP BY S_CODE ) D ON (C.S_CODE = D.S_CODE)
WHERE ROWNUM <= #{criteria.endRow}
ORDER BY distance DESC ) A WHERE A.RN >= #{criteria.startRow}

SELECT
      S_PASSWORD
 FROM STORE
WHERE S_CODE = #{sCode}
  AND S_ID = #{sId}

mapper.update:
UPDATE STORE SET

      S_NAME = #{sName}


    , S_IS_OPEN = #{sIsOpen}


    , S_PHONE = #{sPhone}


    , S_ADDRESS = #{sAddress}


    , S_X = #{sX}


    , S_Y = #{sY}

WHERE S_CODE = #{sCode}

UPDATE STORE SET

    S_PASSWORD = #{sPassword}

WHERE S_CODE = #{sCode}
